import React from 'react'
import styled from 'styled-components'

const CountrySelectedStyled = styled.div`
    margin-top: 3em;
    padding-bottom: 3em;
    .img {
        width: 100%;
        margin-bottom: 2em;
    }
    .grid{
        display: grid;
        grid-row-gap: 1em;
    }
    .border-item {

    }
    .languages {
        span {
            &:after {
                content: ', ';
            } 
            &:last-child {
                &:after {
                    display: none;
                }
            }           
        }
    }
    .borders{

    }
`

function CountrySelected({ 
    flag, 
    name, 
    nativeName, 
    population, 
    region,
    subregion,
    capital,
    topLevelDomain,
    currencies = [],
    languages = [],
    borders,
}) 
    
    {
    return (
        <CountrySelectedStyled>
            <img src={flag} alt="" />
            <div>
                <h2>{name}</h2>
                <div className="grid">
                    <div>
                        <p><strong>Native Name:</strong> {nativeName}</p>
                        <p><strong>Population:</strong> {population}</p>
                        <p><strong>Region:</strong> {region}</p>
                        <p><strong>Sub Region:</strong> {subregion}</p>
                        <p><strong>Capital:</strong> {capital}</p>                    
                    </div>
                    <div>
                        <p><strong>Top Level Domain:</strong> {topLevelDomain}</p>
                        <p><strong>Currencies:</strong> {currencies.map((item) => <span>{item.name}</span>)}</p>
                        <p className="languages"><strong>Languages:</strong> {languages.map((item) => <span>{item.name}</span>)}</p>                    
                    </div>
                </div>
                <p className="borders"><strong>Borders Countries:</strong> </p>
                {borders.map((item) => <span class="border-item">{item}</span>)}
            </div>
        </CountrySelectedStyled>
    )
}

export default CountrySelected
